import { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Outlet } from 'react-router-dom'

// material-ui
import { styled, useTheme } from '@mui/material/styles'
import { AppBar, Box, CssBaseline, Toolbar, useMediaQuery } from '@mui/material'

// project imports
import Breadcrumbs from 'ui-component/extended/Breadcrumbs'
import Header from './Header'
import Sidebar from './Sidebar'
import Customization from '../Customization'
// import navigation from 'menu-items';
// import { 260 } from 'store/constant';
// import { SET_MENU } from 'store/actions'

// assets
import { IconChevronRight } from '@tabler/icons'

// styles
const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })(
  ({ theme, open }) => ({
    ...theme.typography.mainContent,
    ...(!open && {
      borderBottomLeftRadius: 0,
      borderBottomRightRadius: 0,
      // transition: theme.transitions.create('margin', {
      //   easing: theme.transitions.easing.sharp,
      //   duration: theme.transitions.duration.leavingScreen,
      // }),
      [theme.breakpoints.up('md')]: {
        marginLeft: -(260 - 20),
        width: `calc(100% - ${260}px)`,
      },
      [theme.breakpoints.down('md')]: {
        marginLeft: '20px',
        width: `calc(100% - ${260}px)`,
        padding: '16px',
      },
      [theme.breakpoints.down('sm')]: {
        marginLeft: '10px',
        width: `calc(100% - ${260}px)`,
        padding: '16px',
        marginRight: '10px',
      },
    }),
    ...(open && {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
      borderBottomLeftRadius: 0,
      borderBottomRightRadius: 0,
      width: `calc(100% - ${260}px)`,
      [theme.breakpoints.down('md')]: {
        marginLeft: '20px',
      },
      [theme.breakpoints.down('sm')]: {
        marginLeft: '10px',
      },
    }),
  })
)

// ==============================|| MAIN LAYOUT ||============================== //

const MainLayout = () => {
  const theme = useTheme()
  // const matchDownMd = useMediaQuery(theme.breakpoints.down('lg'))
  const matchDownMd = useMediaQuery('(max-width:1200px)')

  // Handle left drawer
  const [toggler, setToggler] = useState(true)
  // const leftDrawerOpened = useSelector((state) => state.customization.opened)
  // const dispatch = useDispatch()
  const handleLeftDrawerToggle = () => {
    // dispatch({ type: SET_MENU, opened: !leftDrawerOpened })
    if (toggler) {
      setToggler(false)
    } else {
      setToggler(true)
    }
  }
  // console.log(leftDrawerOpened)
  useEffect(() => {
    if (matchDownMd) {
      setToggler(false)
    } else {
      setToggler(true)
    }
    // dispatch({ type: SET_MENU, opened: !matchDownMd })
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [matchDownMd])

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      {/* header */}
      <AppBar
        enableColorOnDark
        position='fixed'
        color='inherit'
        elevation={0}
        sx={{
          bgcolor: '#fff',
          // transition: leftDrawerOpened
          transition: toggler
            ? 'box-shadow 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms'
            : 'none',
        }}
      >
        <Toolbar>
          <Header handleLeftDrawerToggle={handleLeftDrawerToggle} />
        </Toolbar>
      </AppBar>

      {/* drawer */}
      <Sidebar drawerOpen={toggler} drawerToggle={handleLeftDrawerToggle} />

      {/* main content */}
      <Main theme={theme} open={true}>
        {/* breadcrumb */}
        {/* <Breadcrumbs separator={IconChevronRight} navigation={navigation} icon title rightAlign /> */}
        <Outlet />
      </Main>
      <Customization />
    </Box>
  )
}

export default MainLayout
